import React from 'react';
import { motion } from 'framer-motion';

const ProductCard = ({ image, title, price, category }) => {
  return (
    <motion.div
      initial={{ opacity: 0, y: 20 }}
      whileInView={{ opacity: 1, y: 0 }}
      viewport={{ once: true }}
      whileHover={{ y: -5 }}
      className="product-card bg-white rounded-xl shadow-lg overflow-hidden group cursor-pointer"
    >
      <div className="relative overflow-hidden">
        <motion.img
          src={image}
          alt={title}
          className="product-image w-full h-64 object-cover"
          whileHover={{ scale: 1.1 }}
          transition={{ duration: 0.3 }}
        />
        <div className="absolute inset-0 bg-gradient-to-t from-black/50 to-transparent opacity-0 group-hover:opacity-100 transition-opacity duration-300" />
      </div>
      
      <div className="p-4">
        <h3 className="font-semibold text-gray-800 mb-2">{title}</h3>
        <div className="flex justify-between items-center">
          <span className="text-kenyan-blue font-bold">{price}</span>
          <span className="text-sm text-gray-500 capitalize">{category}</span>
        </div>
        
        <motion.button
          whileHover={{ scale: 1.05 }}
          whileTap={{ scale: 0.95 }}
          className="w-full mt-3 bg-kenyan-blue text-white py-2 rounded-lg font-medium hover:bg-kenyan-dark-blue transition-colors duration-300"
        >
          View Details
        </motion.button>
      </div>
    </motion.div>
  );
};

export default ProductCard;
